"""
Endstar Mod Patcher - Simple GUI
================================
Select mod file → Click Install → Done!

Mod file format: .esmod (a zip containing bundle + mod.json)
"""

import os
import sys
import json
import base64
import shutil
import zipfile
import tempfile
import tkinter as tk
from tkinter import filedialog, messagebox, ttk
from pathlib import Path
import subprocess
from PIL import Image, ImageTk

# ============================================================
# EMBEDDED ASSETS (Base64 encoded)
# ============================================================

EMBEDDED_LOGO_B64 = """"""

EMBEDDED_ICON_B64 = """"""


# ============================================================
# CONFIGURATION
# ============================================================

TARGET_BUNDLE = 'felix_assets_all_8ce5cfff23e50dfcaa729aa03940bfd7.bundle'
ORIGINAL_CRC = '1004267194'
# Verified original backup location
VERIFIED_BACKUP_PATH = Path(r'D:\Endstar Plot Flag\PLOT FLAG SYSTEM\ORIGINAL_GAME_BACKUPS')

# Will be set by find_game()
GAME_PATH = None
AA_PATH = None

# ============================================================
# GAME DETECTION
# ============================================================

def find_game():
    """Auto-detect Endstar installation anywhere on the system."""
    global GAME_PATH, AA_PATH

    # 1. Check default path first
    default_paths = [
        Path(r'C:\Endless Studios\Endless Launcher\Endstar'),
        Path(r'C:\Program Files\Endless Studios\Endless Launcher\Endstar'),
        Path(r'C:\Program Files (x86)\Endless Studios\Endless Launcher\Endstar'),
    ]

    for p in default_paths:
        if (p / 'Endstar.exe').exists():
            GAME_PATH = p
            AA_PATH = p / 'Endstar_Data' / 'StreamingAssets' / 'aa'
            return True

    # 2. Check Windows Registry for Endless Launcher
    try:
        import winreg
        # Check uninstall registry
        for reg_path in [
            r"SOFTWARE\Microsoft\Windows\CurrentVersion\Uninstall",
            r"SOFTWARE\WOW6432Node\Microsoft\Windows\CurrentVersion\Uninstall"
        ]:
            try:
                with winreg.OpenKey(winreg.HKEY_LOCAL_MACHINE, reg_path) as key:
                    for i in range(1000):
                        try:
                            subkey_name = winreg.EnumKey(key, i)
                            with winreg.OpenKey(key, subkey_name) as subkey:
                                try:
                                    name, _ = winreg.QueryValueEx(subkey, "DisplayName")
                                    if "Endless" in name or "Endstar" in name:
                                        install_path, _ = winreg.QueryValueEx(subkey, "InstallLocation")
                                        endstar_path = Path(install_path)
                                        if not (endstar_path / 'Endstar.exe').exists():
                                            endstar_path = endstar_path / 'Endstar'
                                        if (endstar_path / 'Endstar.exe').exists():
                                            GAME_PATH = endstar_path
                                            AA_PATH = endstar_path / 'Endstar_Data' / 'StreamingAssets' / 'aa'
                                            return True
                                except:
                                    pass
                        except:
                            break
            except:
                pass
    except:
        pass

    # 3. Search common locations
    search_roots = [
        Path(r'C:\\'),
        Path(r'D:\\'),
        Path(r'E:\\'),
        Path(os.environ.get('PROGRAMFILES', r'C:\Program Files')),
        Path(os.environ.get('PROGRAMFILES(X86)', r'C:\Program Files (x86)')),
        Path(os.environ.get('LOCALAPPDATA', '')).parent,
    ]

    search_patterns = [
        'Endless Studios/Endless Launcher/Endstar',
        'Endless Launcher/Endstar',
        'Games/Endstar',
        'Endstar',
    ]

    for root in search_roots:
        if not root.exists():
            continue
        for pattern in search_patterns:
            check_path = root / pattern
            if (check_path / 'Endstar.exe').exists():
                GAME_PATH = check_path
                AA_PATH = check_path / 'Endstar_Data' / 'StreamingAssets' / 'aa'
                return True

    # 4. Search all drives for Endstar.exe (slower but thorough)
    try:
        import string
        for drive in string.ascii_uppercase:
            drive_path = Path(f'{drive}:/')
            if not drive_path.exists():
                continue
            # Check common game folders on each drive
            for folder in ['Endless Studios', 'Games', 'Program Files', 'Program Files (x86)']:
                base = drive_path / folder
                if base.exists():
                    for subdir in base.iterdir():
                        if subdir.is_dir():
                            endstar = subdir / 'Endstar' / 'Endstar.exe'
                            if endstar.exists():
                                GAME_PATH = subdir / 'Endstar'
                                AA_PATH = GAME_PATH / 'Endstar_Data' / 'StreamingAssets' / 'aa'
                                return True
                            endstar = subdir / 'Endstar.exe'
                            if endstar.exists():
                                GAME_PATH = subdir
                                AA_PATH = GAME_PATH / 'Endstar_Data' / 'StreamingAssets' / 'aa'
                                return True
    except:
        pass

    return False

def set_game_path(path):
    """Manually set game path."""
    global GAME_PATH, AA_PATH
    GAME_PATH = Path(path)
    AA_PATH = GAME_PATH / 'Endstar_Data' / 'StreamingAssets' / 'aa'

# ============================================================
# CORE FUNCTIONS
# ============================================================

def check_game_installed():
    """Check if game path is set and valid."""
    return GAME_PATH is not None and (GAME_PATH / 'Endstar.exe').exists()

def create_backup():
    """Backup original files."""
    # Backup catalog
    catalog = AA_PATH / 'catalog.json'
    backup = AA_PATH / 'catalog_original_backup.json'
    if catalog.exists() and not backup.exists():
        shutil.copy(catalog, backup)

    # Backup Felix bundle
    bundle = AA_PATH / 'StandaloneWindows64' / TARGET_BUNDLE
    bundle_backup = AA_PATH / 'StandaloneWindows64' / f'{TARGET_BUNDLE}.original'
    if bundle.exists() and not bundle_backup.exists():
        shutil.copy(bundle, bundle_backup)

def restore_backup():
    """Restore original files."""
    # Use verified backup if available, otherwise fall back to local backup
    verified_catalog = VERIFIED_BACKUP_PATH / 'catalog.json.ORIGINAL'
    verified_bundle = VERIFIED_BACKUP_PATH / f'{TARGET_BUNDLE}.ORIGINAL'

    catalog = AA_PATH / 'catalog.json'
    bundle = AA_PATH / 'StandaloneWindows64' / TARGET_BUNDLE

    # Restore catalog - prefer verified backup
    if verified_catalog.exists():
        shutil.copy(verified_catalog, catalog)
    else:
        backup = AA_PATH / 'catalog_original_backup.json'
        if backup.exists():
            shutil.copy(backup, catalog)

    # Restore Felix bundle - prefer verified backup
    if verified_bundle.exists():
        shutil.copy(verified_bundle, bundle)
    else:
        bundle_backup = AA_PATH / 'StandaloneWindows64' / f'{TARGET_BUNDLE}.original'
        if bundle_backup.exists():
            shutil.copy(bundle_backup, bundle)

def patch_catalog_crc(new_crc):
    """Patch CRC in game catalog."""
    catalog_path = AA_PATH / 'catalog.json'

    # Restore from backup first
    backup = AA_PATH / 'catalog_original_backup.json'
    if backup.exists():
        shutil.copy(backup, catalog_path)

    with open(catalog_path, 'r') as f:
        cat_json = json.load(f)

    extra_data = base64.b64decode(cat_json['m_ExtraDataString'])

    # CRITICAL: Pad new CRC to match original length to preserve catalog structure
    old_str = ORIGINAL_CRC
    new_str = str(new_crc)
    if len(new_str) < len(old_str):
        new_str = new_str + ' ' * (len(old_str) - len(new_str))

    old_bytes = old_str.encode('utf-16-le')
    new_bytes = new_str.encode('utf-16-le')

    if old_bytes not in extra_data:
        raise Exception("Could not find original CRC in catalog")

    extra_data = extra_data.replace(old_bytes, new_bytes, 1)
    cat_json['m_ExtraDataString'] = base64.b64encode(extra_data).decode('ascii')

    with open(catalog_path, 'w') as f:
        json.dump(cat_json, f, separators=(',', ':'))

def read_mod_file(mod_path):
    """
    Read mod file and extract info.
    Supports:
    - .esmod/.zip containing bundle + mod.json
    - folder containing bundle + mod.json
    """
    mod_path = Path(mod_path)

    # If it's a zip/esmod file
    if mod_path.suffix.lower() in ['.esmod', '.zip']:
        with zipfile.ZipFile(mod_path, 'r') as zf:
            # Find mod.json
            if 'mod.json' not in zf.namelist():
                raise Exception("Invalid mod file: missing mod.json")

            mod_info = json.loads(zf.read('mod.json'))

            # Find bundle file
            bundle_name = None
            for name in zf.namelist():
                if name.endswith('.bundle'):
                    bundle_name = name
                    break

            if not bundle_name:
                raise Exception("Invalid mod file: missing .bundle")

            # Extract to temp
            temp_dir = tempfile.mkdtemp()
            zf.extractall(temp_dir)

            mod_info['_bundle_path'] = Path(temp_dir) / bundle_name
            mod_info['_temp_dir'] = temp_dir
            return mod_info

    # If it's a folder
    elif mod_path.is_dir():
        mod_json = mod_path / 'mod.json'
        if not mod_json.exists():
            raise Exception("Invalid mod folder: missing mod.json")

        with open(mod_json) as f:
            mod_info = json.load(f)

        # Find bundle
        bundles = list(mod_path.glob('*.bundle'))
        if not bundles:
            raise Exception("Invalid mod folder: missing .bundle")

        mod_info['_bundle_path'] = bundles[0]
        return mod_info

    else:
        raise Exception("Invalid mod file format")

def install_mod(mod_path):
    """Install mod from file."""
    # Read mod info
    mod_info = read_mod_file(mod_path)

    name = mod_info.get('name', 'Unknown Mod')
    crc = mod_info.get('crc')
    bundle_path = mod_info['_bundle_path']

    if not crc:
        raise Exception("Mod file missing CRC value")

    # Create backups
    create_backup()

    # Copy bundle
    dest = AA_PATH / 'StandaloneWindows64' / TARGET_BUNDLE
    shutil.copy(bundle_path, dest)

    # Patch CRC
    patch_catalog_crc(crc)

    # Cleanup temp if exists
    if '_temp_dir' in mod_info:
        shutil.rmtree(mod_info['_temp_dir'], ignore_errors=True)

    return name

def launch_game():
    """Launch the game."""
    exe = GAME_PATH / 'Endstar.exe'
    if exe.exists():
        subprocess.Popen([str(exe)], cwd=str(GAME_PATH))
        return True
    return False

# ============================================================
# GUI APPLICATION
# ============================================================

def get_resource_path(filename):
    """Get path to resource, works for dev and PyInstaller."""
    if getattr(sys, 'frozen', False):
        # Running as compiled exe - look in same folder as exe
        base_path = Path(sys.executable).parent
    else:
        # Running as script
        base_path = Path(__file__).parent
    return base_path / filename

class ModPatcherApp:
    def __init__(self, root):
        self.root = root
        self.root.title("Endstar Custom Character Installer")
        self.root.geometry("450x360")
        self.root.resizable(False, False)

        # Center window
        self.root.eval('tk::PlaceWindow . center')

        # Set window icon from embedded base64
        try:
            import io
            import tempfile
            icon_data = base64.b64decode(EMBEDDED_ICON_B64)
            # Write to temp file (tkinter needs file path for iconbitmap)
            icon_temp = tempfile.NamedTemporaryFile(suffix='.ico', delete=False)
            icon_temp.write(icon_data)
            icon_temp.close()
            self.root.iconbitmap(default=icon_temp.name)
            self._icon_temp_path = icon_temp.name  # Keep reference for cleanup
        except Exception as e:
            print(f"Icon error: {e}")

        # Load logo image from embedded base64
        self.logo_image = None
        try:
            import io
            logo_data = base64.b64decode(EMBEDDED_LOGO_B64)
            img = Image.open(io.BytesIO(logo_data))
            img = img.resize((200, 60), Image.LANCZOS)
            self.logo_image = ImageTk.PhotoImage(img)
        except Exception as e:
            print(f"Logo error: {e}")

        # Variables
        self.mod_path = tk.StringVar()
        self.mod_name = tk.StringVar(value="No mod selected")
        self.status = tk.StringVar(value="Searching for Endstar...")

        # Build UI first
        self.create_widgets()

        # Then find game (after UI is ready)
        self.root.after(100, self.detect_game)

    def detect_game(self):
        """Detect game installation."""
        self.status.set("Searching for Endstar...")
        self.root.update()

        if find_game():
            self.status.set(f"Found: {GAME_PATH}")
        else:
            # Ask user to locate manually
            result = messagebox.askyesno("Endstar Not Found",
                "Could not auto-detect Endstar installation.\n\n"
                "Would you like to locate it manually?")

            if result:
                self.browse_game()
            else:
                messagebox.showwarning("Warning",
                    "Endstar not found. You can still try to install mods,\n"
                    "but you'll need to locate the game first.")
                self.status.set("Game not found - click to locate")

    def browse_game(self):
        """Let user manually select Endstar.exe."""
        # Start in a sensible location
        initial_dir = None
        if GAME_PATH and GAME_PATH.exists():
            initial_dir = str(GAME_PATH)
        elif Path(r'C:\Endless Studios').exists():
            initial_dir = r'C:\Endless Studios'
        elif Path(r'C:\Program Files').exists():
            initial_dir = r'C:\Program Files'
        else:
            initial_dir = r'C:\\'

        path = filedialog.askopenfilename(
            title="Select Endstar.exe",
            initialdir=initial_dir,
            filetypes=[("Endstar", "Endstar.exe"), ("Executable", "*.exe")]
        )
        if path:
            exe_path = Path(path)
            if exe_path.name.lower() == 'endstar.exe':
                set_game_path(exe_path.parent)
                self.status.set(f"Found: {GAME_PATH}")
                messagebox.showinfo("Success", f"Game found at:\n{GAME_PATH}")
            else:
                messagebox.showerror("Error", "Please select Endstar.exe")

    def create_widgets(self):
        # Main frame
        main = ttk.Frame(self.root, padding="20")
        main.pack(fill=tk.BOTH, expand=True)

        # Logo image
        if self.logo_image:
            logo_label = ttk.Label(main, image=self.logo_image)
            logo_label.pack(pady=(0, 10))

        # Title (simple and clear)
        title = ttk.Label(main, text="Custom Character Installer", font=('Arial', 16, 'bold'))
        title.pack(pady=(0, 15))

        # Mod selection frame
        mod_frame = ttk.LabelFrame(main, text="Mod File", padding="15")
        mod_frame.pack(fill=tk.X, pady=(0, 15))

        # Mod name display
        ttk.Label(mod_frame, textvariable=self.mod_name, font=('Arial', 11, 'bold')).pack(anchor=tk.W)

        # Select button
        ttk.Button(mod_frame, text="Select Mod File (.esmod)", command=self.browse_mod).pack(anchor=tk.W, pady=(10, 0))

        # Buttons frame
        btn_frame = ttk.Frame(main)
        btn_frame.pack(fill=tk.X, pady=15)

        # Big Install button
        install_btn = ttk.Button(btn_frame, text="Install Mod", command=self.install)
        install_btn.pack(side=tk.LEFT, padx=5, expand=True, fill=tk.X)

        uninstall_btn = ttk.Button(btn_frame, text="Uninstall", command=self.uninstall)
        uninstall_btn.pack(side=tk.LEFT, padx=5)

        launch_btn = ttk.Button(btn_frame, text="Launch Game", command=self.launch)
        launch_btn.pack(side=tk.LEFT, padx=5)

        # Status bar with locate button
        status_frame = ttk.Frame(main)
        status_frame.pack(fill=tk.X, side=tk.BOTTOM)

        ttk.Label(status_frame, textvariable=self.status, foreground='#666').pack(side=tk.LEFT)
        locate_btn = ttk.Button(status_frame, text="Browse...", command=self.browse_game, width=10)
        locate_btn.pack(side=tk.RIGHT, padx=(10, 0))

        # Warning
        warn = ttk.Label(main, text="⚠ Launch game directly - NOT via Endless Launcher!",
                        foreground='#cc0000', font=('Arial', 9, 'bold'))
        warn.pack(side=tk.BOTTOM, pady=(10, 0))

    def browse_mod(self):
        path = filedialog.askopenfilename(
            title="Select Mod File",
            filetypes=[
                ("Endstar Mod", "*.esmod"),
                ("Zip files", "*.zip"),
                ("All files", "*.*")
            ]
        )
        if path:
            try:
                mod_info = read_mod_file(path)
                self.mod_path.set(path)
                self.mod_name.set(mod_info.get('name', 'Unknown Mod'))
                self.status.set("Mod selected - ready to install")

                # Cleanup temp if created
                if '_temp_dir' in mod_info:
                    shutil.rmtree(mod_info['_temp_dir'], ignore_errors=True)

            except Exception as e:
                messagebox.showerror("Error", f"Invalid mod file:\n{e}")

    def install(self):
        mod = self.mod_path.get()
        if not mod:
            messagebox.showwarning("Warning", "Please select a mod file first!")
            return

        try:
            self.status.set("Installing...")
            self.root.update()

            name = install_mod(mod)

            self.status.set(f"'{name}' installed!")
            messagebox.showinfo("Success",
                f"'{name}' installed successfully!\n\n"
                "Click 'Launch Game' to play.\n\n"
                "Remember: Launch directly, NOT via Endless Launcher!")

        except Exception as e:
            self.status.set("Installation failed")
            messagebox.showerror("Error", f"Installation failed:\n{e}")

    def uninstall(self):
        try:
            self.status.set("Restoring...")
            self.root.update()

            restore_backup()

            self.status.set("Original files restored")
            self.mod_name.set("No mod selected")
            self.mod_path.set("")
            messagebox.showinfo("Success", "Original game files restored!")

        except Exception as e:
            self.status.set("Restore failed")
            messagebox.showerror("Error", f"Restore failed:\n{e}")

    def launch(self):
        if launch_game():
            self.status.set("Game launched!")
            self.root.after(2000, self.root.destroy)  # Close patcher after 2s
        else:
            messagebox.showerror("Error", "Could not launch game!")

# ============================================================
# MAIN
# ============================================================

def main():
    root = tk.Tk()
    app = ModPatcherApp(root)
    root.mainloop()

if __name__ == '__main__':
    main()
